INSERT INTO `hurriyethaber` (`Id`, `haber_id`, `hash`, `baslik`, `ozet`, `link`, `kurum_list`, `yayin_yeri`, `yayin_tarihi`, `eklenme_tarihi`) VALUES
(1, 'https://www.hurriyet.com.tr/gundem/prof-dr-tirnaksiz-koronaviruse-yenildi-huzunlu-veda-torene-damga-vuran-sozler-bizi-affet-41898951', 'hurriyet', 'Prof. Dr. Tırnaksız koronavirüse yenildi... Hüzünlü veda! Ağlatan detayları meslektaşı paylaştı', 'Hacettepe Üniversitesi Tıp Fakültesi Genel Cerrahi Ana Bilim Dalı öğretim üyesi Prof. Dr. Mehmet Bülent Tırnaksız, koronavirüs nedeniyle hayatını kaybetti. Prof. Dr. Tırnaksız\'ın meslektaşı Prof. Dr. Ömrüm Uzun vefat haberi üzerinde dokunaklı bir yazı paylaştı. Prof. Dr. Uzun, meslektaşı Prof. Dr. Tırnaksız\'ın koronavirüsü nasıl kaptığını açıkladı ve \"Günlük yaşantımızda aldığımız binlerce küçük karardan, binlerce küçük davranıştan biri sadece. Gelin görün ki başkasının hayatına mal olabiliyor\" ifadelerini kullandı.', 'https://www.hurriyet.com.tr/gundem/prof-dr-tirnaksiz-koronaviruse-yenildi-huzunlu-veda-torene-damga-vuran-sozler-bizi-affet-41898951', 'Hacettepe Üniversitesi', 'Hürriyet Haber', 'Mon, 20 Sep 2021 14:04:00 Z', 'Mon, 20 Sep 2021 14:04:00 Z'),
(2, 'https://www.hurriyet.com.tr/gundem/itude-celal-sengor-protestosu-41898948', 'hurriyet', 'İTÜ\'de Prof. Dr. Celal Şengör\'e protesto', 'İstanbul Teknik Üniversitesi öğretim üyesi Prof. Dr. Celal Şengör\'ün bir öğrencisine yönelik cinsel taciz sözleri sosyal medyada büyük tepki çekmişti. İTÜ öğretim üyesi Prof. Dr. Celal Şengör, bir grup öğrenci tarafından protesto edildi.', 'https://www.hurriyet.com.tr/gundem/itude-celal-sengor-protestosu-41898948', 'İstanbul Teknik Üniversitesi', 'Hürriyet Haber', 'Mon, 20 Sep 2021 11:35:00 Z', 'Mon, 20 Sep 2021 11:35:00 Z'),
